// --- MIDI spec hard coded values ---// good reference is here: https://www.music.mcgill.ca/~ich/classes/mumt306/StandardMIDIfileformat.html#BM1_1// from the midi.org itself: https://midi.org/summary-of-midi-1-0-messages#define MetaEndOfTrack      0x2F#define MetaSequence        0x00#define MetaText            0x01#define MetaCopyright       0x02#define MetaTrackName       0x03#define MetaInstrumentName  0x04#define MetaLyrics          0x05#define MetaMarker          0x06#define MetaCuePoint        0x07#define MetaChannelPrefix   0x20#define MetaChangePort      0x21#define MetaEndOfTrack      0x2F#define MetaSetTempo        0x51#define MetaSMPTEOffset     0x54#define MetaTimeSignature   0x58#define MetaKeySignature    0x59#define MetaSequencerSpecific  0x7Ftypedef struct aMIDIEvent {    long deltaToGo;    int bytecount;    char msgToSend[3]; //some events are 2 bytes, some are 3, no biggie to use worst-case scenario    } aME, *aMEPtr, **aMEH;    typedef struct aTableOfEvent {	int trackno;	int eventcount; //keeps track of total events for playback	int eventleft; //rises when building up, lowers when playing back	aMEPtr trackEvents;	} aTOE, *aTOEPtr, **aTOEH;	typedef struct bigParsedEventList {	int trackcount;	aTOEPtr TrackEventList;	Boolean hasBeenUsed;	} bigParsed, *bigParsedPtr, **bigParsedH;