#define kPiano 21198#define kClick 740#define kSndOffset 36#include <Sound.h>#include "Misc.c"void main(void){Handle tempH,sndH;Ptr myPtr;FFSynthPtr sndPtr;int loop;long bufferSize,addSize;Str255 str;InitMacStuff();HideMyMenuBar();WholeScreen(white,false);sndH = GetResource('snd ', kPiano);bufferSize = GetHandleSize(sndH) - kSndOffset;if(bufferSize % kClick)  {  addSize = (bufferSize/kClick + 1) * kClick -bufferSize;  }bufferSize += addSize;tempH = NewHandleClear(addSize);HandAndHand(tempH,sndH);HLock((Handle)sndH);myPtr = *sndH + kSndOffset/4;sndPtr = (FFSynthPtr)myPtr;sndPtr->mode = ffMode;while(!Button()) {sndPtr->count = FixRatio(rangeRandom(1,3),rangeRandom(2,6));while(!SoundDone());StopSound();StartSound(sndPtr,bufferSize,nil); }HUnlock(sndH);while(!Button());}